import React, {useContext, useEffect, useState} from 'react';
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {Context} from "../index";
import moduleStyle from "../modules/Admin.module.css"


const Admin = () => {
    const {dish} = useContext(Context)
    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const [info, setInfo] = useState([])

    const addInfo = () => {
        setInfo([...info, {title: '', amount: '', meaSure: '', additionally: '', number: Date.now()}])
    }

    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }

    return (
        <Form className={moduleStyle.form}>
            <h1>Добавление рецепта</h1>
            <Dropdown className="mt-2 mb-2">
                <Dropdown.Toggle>{dish.selectedCategory.title || "Выберите категорию"}</Dropdown.Toggle>
                <Dropdown.Menu>
                    {dish.categories.map(category =>
                        <Dropdown.Item
                            onClick={() => dish.setSelectedCategory(category)}
                            key={category.id}
                        >
                            {category.title}
                        </Dropdown.Item>
                    )}
                </Dropdown.Menu>
            </Dropdown>
            <Dropdown className="mt-2 mb-2">
                <Dropdown.Toggle>{dish.selectedNationality.title || "Выберите кухню"}</Dropdown.Toggle>
                <Dropdown.Menu>
                    {dish.nationalities.map(nationality =>
                        <Dropdown.Item
                            onClick={() => dish.setSelectedNationality(nationality)}
                            key={nationality.id}
                        >
                            {nationality.title}
                        </Dropdown.Item>
                    )}
                </Dropdown.Menu>
            </Dropdown>
            <Dropdown className="mt-2 mb-2">
                <Dropdown.Toggle>{dish.selectedSpecialGroup.title || "Выберите назначение"}</Dropdown.Toggle>
                <Dropdown.Menu>
                    {dish.specialGroup.map(specialGroup =>
                        <Dropdown.Item
                            onClick={() => dish.setSelectedSpecialGroup(specialGroup)}
                            key={specialGroup.id}
                        >
                            {specialGroup.title}
                        </Dropdown.Item>
                    )}
                </Dropdown.Menu>
            </Dropdown>
            <h4>Название</h4>
            <Form.Control placeholder="Название блюда"/>
            <h4>Инструкция приготовления</h4>
            <Form.Control as="textarea" aria-rowcount={3} placeholder="Инструкция приготовления"/>
            <h4>Время приготовления</h4>
            <Form.Control placeholder="Примерное время (мин)"/>
            <hr/>
            <h4>Фотография</h4>
            <Form.Control type="file"/>
            <hr/>
            {info.map(i =>
                <Row className="mt-4" key={i.number}>
                    <Col md={3}>
                        <Form.Control
                            value={i.title}
                            onChange={(e) => changeInfo('title', e.target.value, i.number)}
                            placeholder="Введите название ингредиента"
                        />
                    </Col>
                    <Col md={1}>
                        <Form.Control
                            value={i.amount}
                            onChange={(e) => changeInfo('amount', e.target.value, i.number)}
                            placeholder="Кол-во"
                        />
                    </Col>
                    <Col md={1}>
                        <Form.Control
                            value={i.meaSure}
                            onChange={(e) => changeInfo('meaSure', e.target.value, i.number)}
                            placeholder="Объём"
                        />
                    </Col>
                    <Col md={3}>
                        <Form.Control
                            value={i.additionally}
                            onChange={(e) => changeInfo('additionally', e.target.value, i.number)}
                            placeholder="Примечание"
                        />
                    </Col>
                    <Col md={1}>
                        <Button
                            onClick={() => removeInfo(i.number)}
                            variant={"outline-danger"}
                        >
                            ❌
                        </Button>
                    </Col>
                </Row>
            )}
            <Button className="mt-4" variant={"outline-dark"} onClick={addInfo}>Добавить ингридиент</Button>
        </Form>
    );
};

export default Admin;
