import {makeAutoObservable} from "mobx";

export default class DishStore{
    constructor() {
        this._categories = [
            {id: 1, title: "Суп"},
            {id: 2, title: "Выпечка"},
        ]
        this._selectedCategory = {}
        this._nationalities = [
            {id: 1, title: "Русская"},
            {id: 2, title: "Итальянская"},
        ]
        this._selectedNationality = {}
        this._specialGroup = [
            {id: 1, title: "Для детей"},
            {id: 2, title: "Без глютена"},
        ]
        this._selectedSpecialGroup = {}
        this._dishes = [
            {id: 1, name: "Суши", img: "https://kartinkin.net/uploads/posts/2022-03/1646956202_9-kartinkin-net-p-sushi-rolli-kartinki-9.jpg",
                description:"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
            user: "Dima", comments_num: 5, }
        ]
        this._page = 1
        this._totalCount = 0
        this._limit = 3
        makeAutoObservable(this)
    }

    setCategories(categories) {
        this._categories = categories
    }
    setNationalities(nationalities) {
        this._nationalities = nationalities
    }
    setSpecialGroup(specialGroup) {
        this._specialGroup = specialGroup
    }
    setDish(dishes) {
        this._dishes = dishes
    }
    setSelectedCategory(category) {
        this.setPage(1)
        this._selectedCategory = category
    }
    setSelectedSpecialGroup(specialGroup) {
        this.setPage(1)
        this._selectedSpecialGroup = specialGroup
    }
    setSelectedNationality(nationality) {
        this.setPage(1)
        this._selectedNationality = nationality
    }
    setPage(page) {
        this._page = page
    }
    setTotalCount(count) {
        this._totalCount = count
    }


    get categories(){
        return this._categories
    }
    get nationalities() {
        return this._nationalities
    }
    get specialGroup() {
        return this._specialGroup
    }
    get dishes() {
        return this._dishes
    }
    get selectedCategory() {
        return this._selectedCategory
    }
    get selectedNationality() {
        return this._selectedNationality
    }
    get selectedSpecialGroup() {
        return this._selectedSpecialGroup
    }
    get totalCount() {
        return this._totalCount
    }
    get page() {
        return this._page
    }
    get limit() {
        return this._limit
    }
}
