import React, {useEffect, useState} from 'react';
import { Card, Col, Container, Row} from "react-bootstrap";
import {useParams} from "react-router-dom";
import {fetchOneDish} from "../http/dishAPI";


const DishPage = () => {
    const [dish, setDish] = useState({info: []})
    const {id} = useParams()

    let options = {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: 'numeric'
    }



    useEffect(() => {
        fetchOneDish(id).then(data => setDish(data[0]))
    }, [])

    return (
        <Container>
            <Row className='mt-3'>
                <Col md={3}>
                    <Card style={{border: 'none', width: '500px', height: '200px'}}>
                        <Card.Body>
                            <Card.Text>
                                <h1>{dish.title}</h1>
                                <h4>Рецепт от: {dish.nickname}</h4>
                            </Card.Text>
                        </Card.Body>
                        <Card.Img variant="bottom" src={process.env.REACT_APP_API_URL + dish.img} style={{backgroundSize: "cover", width: '400px'}}/>
                    </Card>
                </Col>
                <Col md={3}>
                    <h2>Как приготовить</h2>
                </Col>
            </Row>
            <Row className="mt-5">
                {/*<h2>Характеристики</h2>*/}
                {/*{dish.info.map((info, index) =>*/}
                {/*    <Row key={info.id} style={{backgroundColor: index % 2 === 0 ? '#808B96 ' : 'transparent', padding: 10}}>*/}
                {/*        {info.title} : {info.description}*/}
                {/*    </Row>*/}
                {/*)}*/}
            </Row>
        </Container>
    );
};

export default DishPage;
