import React, {useContext, useEffect} from 'react';
import background from '../assets/background.jpg'
import {Button, Form, ListGroup, Row} from "react-bootstrap";
import moduleStyle from "../modules/Search.module.css";
import NationalityBar from "./NationalityBar";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {fetchIngridients} from "../http/dishAPI";

const Search = observer(() => {
    const {dish} = useContext(Context)

    const search = () =>
    {
        console.log(dish.ingridientSearch)
    }

    useEffect(() => {
        fetchIngridients().then(data => dish.setIngridient(data))
    }, [])


    return (
        <div className={moduleStyle.cover} style={{
            background: `url(${background})`,
        }}>
            <Form className="d-flex flex-column">
                <Row className={moduleStyle.searchBar}>
                    <input value={i.ingridient_id} list="ingridients" placeholder="Ингредиент"/>
                    {/*<Form.Control className={moduleStyle.input} style={{borderRadius: '25px'}}*/}
                    {/*              placeholder="Поиск по рецептам"/>*/}
                    {/*<Button className={moduleStyle.button} variant="primary" onClick={search}>Найти</Button>*/}
                </Row>
            </Form>
            <NationalityBar/>
        </div>
    );
});

export default Search;
