import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {Card, Row} from "react-bootstrap";

const NationalityBar = observer(() => {
    const {dish} = useContext(Context)
    return (
        <Row className="d-flex">
            {dish.map(nationality => <Card
                key={nationality.id} className="p-3 mb-3"
                style={{cursor: "pointer", width: '15rem' }}
                border={nationality.id === dish.selectedNationality.id ? 'danger' : 'light'}
                onClick={() => dish.setSelectedNationality(nationality)}>{nationality.title}</Card>)}
        </Row>
    );
});

export default NationalityBar;
