import React, {useContext, useEffect, useState} from 'react';
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {Context} from "../index";
import moduleStyle from "../modules/Admin.module.css"


const Admin = () => {
    const {dish} = useContext(Context)
    const [title, setTitle] = useState('')
    const [cookingTime, setCookingTime] = useState(0)
    const [description, setDescription] = useState('')
    const [file, setFile] = useState(null)

    const addInfo = () => {
        setDescription([...description, {title: '', amount: '', meaSure: '', additionally: '', number: Date.now()}])
    }

    const removeInfo = (number) => {
        setDescription(description.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setDescription(description.map(i => i.number === number ? {...i, [key]: value} : i))
    }

    const selectFile = () => {
        const formData = new FormData()
        formData.append('title', title)
        formData.append('description', description)
        formData.append('cookingTime', `${cookingTime}`)
        formData.append('img', file)
        formData.append('category_id', dish.selectedCategory.id)
        formData.append('category_id', dish.selectedNationality.id)
        formData.append('typeId', dish.selectedSpecialGroup.id)
        formData.append('info', JSON.stringify(info))
        createDevice(formData).then(data => onHide())
    }

    return (
        <div className={moduleStyle.form}>
            <Form >
                <h1>Добавление рецепта</h1>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedCategory.title || "Выберите категорию"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.categories.map(category =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedCategory(category)}
                                key={category.id}
                            >
                                {category.title}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedNationality.title || "Выберите кухню"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.nationalities.map(nationality =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedNationality(nationality)}
                                key={nationality.id}
                            >
                                {nationality.title}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedSpecialGroup.title || "Выберите назначение"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.specialGroup.map(specialGroup =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedSpecialGroup(specialGroup)}
                                key={specialGroup.id}
                            >
                                {specialGroup.title}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <h4>Название</h4>
                <Form.Control value={title} onChange={setTitle(e.target.value)} placeholder="Название блюда"/>
                <h4>Инструкция приготовления</h4>
                <Form.Control as="textarea" value={description} onChange={setDescription(e.target.value)} placeholder="Инструкция приготовления"/>
                <h4>Время приготовления</h4>
                <Form.Control value={cookingTime} onChange={setCookingTime(e.target.value)} placeholder="Примерное время (мин)"/>
                <hr/>
                <h4>Фотография</h4>
                <Form.Control onChange={selectFile} type="file"/>
                <hr/>
                {info.map(i =>
                    <Row className="mt-4" key={i.number}>
                        <Col md={3}>
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                placeholder="Введите название ингредиента"
                            />
                        </Col>
                        <Col md={1}>
                            <Form.Control
                                value={i.amount}
                                onChange={(e) => changeInfo('amount', e.target.value, i.number)}
                                placeholder="Кол-во"
                            />
                        </Col>
                        <Col md={1}>
                            <Form.Control
                                value={i.meaSure}
                                onChange={(e) => changeInfo('meaSure', e.target.value, i.number)}
                                placeholder="Объём"
                            />
                        </Col>
                        <Col md={3}>
                            <Form.Control
                                value={i.additionally}
                                onChange={(e) => changeInfo('additionally', e.target.value, i.number)}
                                placeholder="Примечание"
                            />
                        </Col>
                        <Col md={1}>
                            <Button
                                onClick={() => removeInfo(i.number)}
                                variant={"outline-danger"}
                            >
                                ❌
                            </Button>
                        </Col>
                    </Row>
                )}
                <Button className="mt-3" variant={"outline-dark"} onClick={addInfo}>Добавить ингридиент</Button>
            </Form>
            <Button className="mt-4" variant="outline-success">Добавить</Button>
        </div>
    );
};

export default Admin;
