import React, {useContext, useEffect, useState} from 'react';
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {Context} from "../index";
import moduleStyle from "../modules/Admin.module.css"
import {createDish, fetchCategory, fetchMeaSure, fetchNationality, fetchSpecialGroup} from "../http/dishAPI";
import {observer} from "mobx-react-lite";
import jwt_decode from "jwt-decode";


const Admin = observer(() => {
    const {dish} = useContext(Context)
    const [title, setTitle] = useState('')
    const [cookingTime, setCookingTime] = useState(0)
    const [description, setDescription] = useState('')
    const [info, setInfo] = useState([])
    const [file, setFile] = useState(null)

    let token = localStorage.getItem(localStorage.key(0))
    let userInfo = jwt_decode(token);
    let userId = userInfo.id

    useEffect(() => {
        fetchCategory().then(data => dish.setCategories(data))
        fetchNationality().then(data => dish.setNationalities(data))
        fetchSpecialGroup().then(data => dish.setSpecialGroup(data))
        fetchMeaSure().then(data => dish.setMeaSure(data))
    }, [])

    const addInfo = () => {
        setInfo([...info, {title: '', amount: '', meaSure: 0, additionally: '', number: Date.now()}])
    }

    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
        console.log(dish.setSelectedMeaSure(meaSure))
    }

    const selectFile = e => {
        setFile(e.target.files[0])
    }

    const addDish = () => {
        const formData = new FormData()
        formData.append('title', title)
        formData.append('description', description)
        formData.append('cooking_time', `${cookingTime}`)
        formData.append('img', file)
        formData.append('category_id', dish.selectedCategory.id)
        formData.append('nationality_id', dish.selectedNationality.id)
        formData.append('special_group_id', dish.selectedSpecialGroup.id)
        formData.append('info', JSON.stringify(info))
        formData.append('user_id', userId)
        createDish(formData).then(data => console.log(formData))
    }

    return (
        <div className={moduleStyle.form}>
            <Form >
                <h1>Добавление рецепта</h1>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedCategory.title || "Выберите категорию"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.categories.map(category =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedCategory(category)}
                                key={category.id}
                            >
                                {category.title}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedNationality.name || "Выберите кухню"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.nationalities.map(nationality =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedNationality(nationality)}
                                key={nationality.id}
                            >
                                {nationality.name}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <Dropdown className="mt-2 mb-2">
                    <Dropdown.Toggle>{dish.selectedSpecialGroup.name || "Выберите назначение"}</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {dish.specialGroup.map(specialGroup =>
                            <Dropdown.Item
                                onClick={() => dish.setSelectedSpecialGroup(specialGroup)}
                                key={specialGroup.id}
                            >
                                {specialGroup.name}
                            </Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
                <h4>Название</h4>
                <Form.Control value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Название блюда"/>
                <h4>Инструкция приготовления</h4>
                <Form.Control as="textarea" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Инструкция приготовления"/>
                <h4>Время приготовления (мин)</h4>
                <Form.Control value={cookingTime} onChange={(e) => setCookingTime(Number(e.target.value))} placeholder="Примерное время (мин)"/>
                <hr/>
                <h4>Фотография</h4>
                <Form.Control onChange={selectFile} type="file"/>
                <hr/>
                {info.map(i =>
                    <Row className="mt-4" key={i.number}>
                        <Col md={3}>
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                placeholder="Введите название ингредиента"
                            />
                        </Col>
                        <Col md={1}>
                            <Form.Control
                                value={i.amount}
                                onChange={(e) => changeInfo('amount', e.target.value, i.number)}
                                placeholder="Кол-во"
                            />
                        </Col>
                        <Col md={1}>
                            <Dropdown  drop="up">
                                <Dropdown.Toggle>{dish.selectedMeaSure.title || "Объём"}</Dropdown.Toggle>
                                <Dropdown.Menu>
                                    {dish.meaSure.map(meaSure =>
                                        <Dropdown.Item
                                            onChange={(e) => changeInfo('meaSure', e.target.value, i.number)}
                                            onClick={() => dish.setSelectedMeaSure(meaSure)}
                                            key={meaSure.id}
                                        >
                                            {meaSure.title}
                                        </Dropdown.Item>
                                    )}
                                </Dropdown.Menu>
                            </Dropdown>
                            {/*<Form.Control*/}
                            {/*    value={i.meaSure}*/}
                            {/*    onChange={(e) => changeInfo('meaSure', e.target.value, i.number)}*/}
                            {/*    placeholder="Объём"*/}
                            {/*/>*/}
                        </Col>
                        <Col md={3}>
                            <Form.Control
                                value={i.additionally}
                                onChange={(e) => changeInfo('additionally', e.target.value, i.number)}
                                placeholder="Примечание"
                            />
                        </Col>
                        <Col md={1}>
                            <Button
                                onClick={() => removeInfo(i.number)}
                                variant={"outline-danger"}
                            >
                                ❌
                            </Button>
                        </Col>
                    </Row>
                )}
                <Button className="mt-3" variant={"outline-dark"} onClick={addInfo}>Добавить ингридиент</Button>
            </Form>
            <Button className="mt-4" variant="outline-success" onClick={addDish}>Добавить</Button>
        </div>
    );
});

export default Admin;
