import React from 'react';
import background from '../assets/background.jpg'
import {Button, Form} from "react-bootstrap";
import moduleStyle from "../modules/Search.module.css";
import NationalityBar from "./NationalityBar";
import {useState} from 'react';
import {observer} from "mobx-react-lite";

const Search = observer(() => {
    const [ingridientsArray, setIngridientsArray] = useState('')

    return (
        <div className={moduleStyle.cover} style={{
            background: `url(${background})`,
        }}>
            <Form className="d-flex flex-column">
                <Form.Control value={ingridientsArray} onChange={(e) => setIngridientsArray(e.target.value)}
                    className={moduleStyle.input} style={{borderRadius: '25px'}} placeholder="Поиск по рецептам" />
                <Button className="mt-3" variant="outline-primary" onClick={setIngridientsArray}>Найти</Button>
            </Form>
            <NationalityBar/>
        </div>
    );
});

export default Search;
