import React, {useContext, useEffect, useState} from 'react';
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";


const Admin = () => {
    const {dish} = useContext(Context)
    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const [info, setInfo] = useState([])

    return (
        <Form>
            <Dropdown className="mt-2 mb-2">
                <Dropdown.Toggle>{dish.selectedCategory.name || "Выберите категорию"}</Dropdown.Toggle>
                <Dropdown.Menu>
                    {dish.categories.map(category =>
                        <Dropdown.Item
                            onClick={() => dish.setSelectedCategory(category)}
                            key={category.id}
                        >
                            {category.title}
                        </Dropdown.Item>
                    )}
                </Dropdown.Menu>
            </Dropdown>
            <Dropdown className="mt-2 mb-2">
                <Dropdown.Toggle>{dish.selectedNationality.name || "Выберите кухню"}</Dropdown.Toggle>
                <Dropdown.Menu>
                    {dish.nationalities.map(nationality =>
                        <Dropdown.Item
                            onClick={() => dish.setSelectedBrand(nationality)}
                            key={nationality.id}
                        >
                            {nationality.name}
                        </Dropdown.Item>
                    )}
                </Dropdown.Menu>
            </Dropdown>
            <Form.Control
                value={name}
                onChange={e => setName(e.target.value)}
                className="mt-3"
                placeholder="Введите название рецепта"
            />
            <Form.Control
                value={price}
                onChange={e => setPrice(Number(e.target.value))}
                className="mt-3"
                placeholder="Введите время приготовления"
                type="number"
            />
            <Form.Control
                className="mt-3"
                type="file"
                onChange={selectFile}
            />
            <hr/>
            <Button
                variant={"outline-dark"}
                onClick={addInfo}
            >
                Добавить новое свойство
            </Button>
            {info.map(i =>
                <Row className="mt-4" key={i.number}>
                    <Col md={4}>
                        <Form.Control
                            value={i.title}
                            onChange={(e) => changeInfo('title', e.target.value, i.number)}
                            placeholder="Введите название свойства"
                        />
                    </Col>
                    <Col md={4}>
                        <Form.Control
                            value={i.description}
                            onChange={(e) => changeInfo('description', e.target.value, i.number)}
                            placeholder="Введите описание свойства"
                        />
                    </Col>
                    <Col md={4}>
                        <Button
                            onClick={() => removeInfo(i.number)}
                            variant={"outline-danger"}
                        >
                            Удалить
                        </Button>
                    </Col>
                </Row>
            )}
        </Form>
    );
};

export default Admin;
