import React, {useEffect, useState} from 'react';
import {Col, Container, Image, Row} from "react-bootstrap";
import {useParams} from "react-router-dom";
import {fetchOneDish} from "../http/dishAPI";


const DishPage = () => {
    const [dish, setDish] = useState({info: []})
    const {id} = useParams()

    let options = {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: 'numeric'
    }


    useEffect(() => {
        fetchOneDish(id).then(data => setDish(data[0]))
    }, [])

    return (
        <Container>
            <Row className='mt-3'>
                <Col md={6}>
                    <h1>{dish.title}</h1>
                    <h4>Рецепт от: {dish.nickname}</h4>
                    <Image src={process.env.REACT_APP_API_URL + dish.img}
                           style={{backgroundSize: "cover", width: '400px'}}/>
                </Col>
                <Col md={3}>
                    <h2>Как приготовить</h2>
                    <p>{dish.description}</p>
                </Col>
            </Row>
            <Row className="mt-5">
                <h2>Ингредиенты</h2>
                {/*{dish.info.map((info, index) =>*/}
                {/*    <Row key={info.id} style={{backgroundColor: index % 2 === 0 ? '#808B96 ' : 'transparent', padding: 10}}>*/}
                {/*        {info.title} : {info.description}*/}
                {/*    </Row>*/}
                {/*)}*/}
            </Row>
        </Container>
    );
};

export default DishPage;
