import React, {useContext, useEffect} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import CategoryBar from "../components/CategoryBar";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {fetchCategory, fetchDishes, fetchNationality, fetchSpecialGroup} from "../http/dishAPI";
import SpecialGroupBar from "../components/SpecialGroupBar";
import DishList from "../components/DishList";
import Search from "../components/Search";

const Shop = observer(() => {
    const {dish} = useContext(Context)
    useEffect(() => {
        fetchCategory().then(data => dish.setCategories(data))
        fetchNationality().then(data => dish.setNationalities(data))
        fetchSpecialGroup().then(data => dish.setSpecialGroup(data))
        fetchDishes().then(data => {
            dish.setDish(data)
            // dish.setTotalCount(data.count)
        })
    }, [])
    //
    // useEffect(() => {
    //     fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 2).then(data => {
    //         device.setDevice(data.rows)
    //         device.setTotalCount(data.count)
    //     })
    // }, [device.page, device.selectedType, device.selectedBrand])

    return (
        <Container>
            <Row className="mt-3">
                <Search/>
            </Row>
            <Row className="mt-3">
                <Col md={9}>
                    <DishList/>
                </Col>
                <Col md={3}>
                    <CategoryBar/>
                    <SpecialGroupBar className="mt-3"/>
                    <Pages/>
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
