import React from 'react';
import {Button, Card, Col} from "react-bootstrap";
import {useHistory} from "react-router-dom";
import {DISH_ROUTE} from "../utils/const";

const DeviceItem = ({dish}) => {
    const history = useHistory()
    let description = dish.name
    return (
        <Col md={3}>
            <Card className="mt-3" style={{width: 200, cursor: 'pointer', border: 'none'}}
                  onClick={() => history.push(DISH_ROUTE + '/' + dish.id)}
            >
                <Card.Img variant="top" src={
                    // process.env.REACT_APP_API_URL +
                    dish.img}
                          style={{width: 200, height: 220}}/>
                <Card.Body>
                    <Card.Title>{dish.name}</Card.Title>
                    <Card.Text>{description.substr(0, 10)}</Card.Text>
                </Card.Body>
            </Card>
        </Col>
    );
};

export default DeviceItem;
