import React, {useContext} from 'react';
import background from '../assets/background.jpg'
import {Button, Form, ListGroup, Row} from "react-bootstrap";
import moduleStyle from "../modules/Search.module.css";
import NationalityBar from "./NationalityBar";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {useEffect} from "@types/react";
import {fetchIngridients} from "../http/dishAPI";

const Search = observer(() => {
    const {dish} = useContext(Context)

    const search = (ingridientsArray) => {
        console.log(ingridientsArray)
    }

    useEffect(() => {
        fetchIngridients().then(data => dish.setIngridient(data))
    }, [])


    return (
        <div className={moduleStyle.cover} style={{
            background: `url(${background})`,
        }}>
            <Form className="d-flex flex-column">
                <Row className={moduleStyle.searchBar}>
                    <input value={i.ingridient_id} list="ingridients" placeholder="Ингредиент"
                           onChange={(e) => changeInfo('ingridient_id', e.target.value, i.number)}/>
                    <datalist id="ingridients">
                        {dish.ingridient.map(ingridient =>
                            <option value={ingridient.id} onClick={() => dish.setSelectedIngridient(ingridient)}
                                    key={ingridient.id}>{ingridient.title}</option>)}
                    </datalist>
                    {/*<Form.Control value={''} onChange={} className={moduleStyle.input} style={{borderRadius: '25px'}}*/}
                    {/*              placeholder="Поиск по рецептам"/>*/}
                    {/*<Button className={moduleStyle.button} variant="primary" onClick={search}>Найти</Button>*/}
                </Row>
            </Form>
            {/*<ListGroup className="mt-3">*/}
            {/*    <h3>Категории</h3>*/}
            {/*    {dish.categories.map(category => <ListGroup.Item*/}
            {/*        style={{cursor: "pointer"}}*/}
            {/*        active={category.id === dish.selectedCategory.id}*/}
            {/*        onClick={() => dish.setSelectedCategory(category)}*/}
            {/*        key={category.id}>{category.title}</ListGroup.Item>)}*/}
            {/*</ListGroup>*/}
            <NationalityBar/>
        </div>
    );
});

export default Search;
