import {$authHost, $host} from "./index";

export const fetchCategory = async () => {
    const {data} = await $host.get('api/category')
    return data
}

export const fetchNationality = async () => {
    const {data} = await $host.get('api/nationality')
    return data
}

export const fetchSpecialGroup = async () => {
    const {data} = await $host.get('api/specialgroup')
    return data
}
//
// export const fetchOneDish = async (id) => {
//     const {data} = await $host.get('api/dish/' + id)
//     return data
// }
//
export const fetchDishes = async (categoryId, nationalityId, specialGroupId, page, limit= 5) => {
    const {data} = await $host.get('api/dish', {params: {
            categoryId, nationalityId, specialGroupId, page, limit
        }})
    return data
}
