import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import {useParams} from "react-router-dom";
import {fetchOneDevice} from "../http/deviceAPI";

const DishPage = () => {
    const [device, setDevice] = useState({info: []})
    const {id} = useParams()

    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data))
    }, [])
    return (
        <Container>
            <Row className='mt-3'>
                <Col md={4}>
                    <Image weight={300} height={300} src={process.env.REACT_APP_API_URL + device.img}/>
                </Col>
                <Col md={4}>
                    <h1>{device.name}</h1>
                    <p>Рейтинг: {device.rating}</p>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 150, fontSize: 32, color: 'whitesmoke', border: 'none', backgroundColor: '#808B96 '}}>
                        От {device.price} руб.
                        <Button variant={"outline-dark"}>Добавить в избранное</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="mt-5">
                <h2>Характеристики</h2>
                {device.info.map((info, index) =>
                    <Row key={info.id} style={{backgroundColor: index % 2 === 0 ? '#808B96 ' : 'transparent', padding: 10}}>
                        {info.title} : {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DishPage;
