import React from 'react';
import background from '../assets/background.jpg'
import {Form} from "react-bootstrap";

const Search = () => {
    let bg = 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=870&q=80'
    return (
        <div style={{
            background: `url(${background})`,
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center center',
            height: '600px',
            backgroundSize: 'cover',
            borderRadius: '25px'
        }}>
            <Form>
                <Form.Control type="email" placeholder="Поиск по рецептам" />
            </Form>
        </div>
    );
};

export default Search;
