import React, {useContext} from 'react';
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {ListGroup} from "react-bootstrap";

const NationalityBar = observer(() => {
    const {dish} = useContext(Context)
    return (
        <ListGroup>
            {dish.nationalities.map(nationality => <ListGroup.Item
                style={{cursor: "pointer"}}
                active={nationality.id === dish.selectedNationality.id}
                onClick={() => dish.setSelectedNationality(nationality)}
                key={nationality.id}>{nationality.title}</ListGroup.Item>)}
        </ListGroup>
    );
});

export default NationalityBar;
